import React from "react";
import Header from "./Header.js";
import "../index.css";
import Main from "./Main";
import Footer from "./Footer.js";
import ImagePopup from "./ImagePopup";
import PopupWithForm from "./PopupWithForm";
import CurrentUserContext from '../contexts/CurrentUserContext.js';
import api from "../utils/api.js";

export default function App() {
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] =
        React.useState(false);
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] =
        React.useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = React.useState(false);
    const [selectedCard, setSelectedCard] = React.useState(null);
    const [currentUser, setCurrentUser] = React.useState({});
    const [cards, setCards] = React.useState([]);

    function closeAllPopups() {
        setIsEditProfilePopupOpen(false);
        setIsEditAvatarPopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setSelectedCard(null);
    }

    React.useEffect(() => {
        Promise.all([api.getUserInfoApi(), api.getInitialCardsApi()])
            .then(([userInfo, cardData]) => {
                setCurrentUser(userInfo)
                setCards(cardData)
            }).catch(err => {
                console.log(err);
            });
    }, [])

    function handleCardLike(card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
            setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        });
    }

    return (
        <CurrentUserContext.Provider value={currentUser}>
        <div className="body">
            <div className="page">
                <Header />
                <Main
                    onEditAvatar={setIsEditAvatarPopupOpen}
                    onEditProfile={setIsEditProfilePopupOpen}
                    onAddPlace={setIsAddPlacePopupOpen}
                    onCardClick={setSelectedCard}
                    cards={cards}
                />
                <Footer />
                <PopupWithForm
                    isOpen={isEditProfilePopupOpen}
                    onClose={closeAllPopups}
                    name="edit-profile"
                    title="Редактировать профиль"
                    button="Сохранить"
                >
                    <input
                        id="nameInput"
                        type="text"
                        className="popup__input popup__input_user_name"
                        name="name"
                        placeholder="Имя пользователя"
                        required
                        minLength="2"
                        maxLength="40"
                    />
                    <span className="popup__form-error popup__form-error-name"></span>
                    <input
                        id="jobInput"
                        type="text"
                        className="popup__input popup__input_user_job"
                        name="about"
                        placeholder="Род деятельности"
                        required
                        minLength="2"
                        maxLength="200"
                    />
                    <span className="popup__form-error popup__form-error-about"></span>
                </PopupWithForm>
                <PopupWithForm
                    isOpen={isAddPlacePopupOpen}
                    onClose={closeAllPopups}
                    name="add-place"
                    title="Новое место"
                    button="Создать"
                >
                    <input
                        id="textInput"
                        type="text"
                        className="popup__input  popup__input_mesto_title"
                        name="title"
                        placeholder="Название"
                        required
                        minLength="2"
                        maxLength="30"
                    />
                    <span className="popup__form-error popup__form-error-title"></span>
                    <input
                        id="imageInput"
                        type="url"
                        className="popup__input popup__input_mesto_image"
                        name="image"
                        placeholder="Ссылка на картинку"
                        required
                    />
                    <span className="popup__form-error popup__form-error-image"></span>
                </PopupWithForm>
                <PopupWithForm
                    isOpen={isEditAvatarPopupOpen}
                    onClose={closeAllPopups}
                    name="avatar"
                    title="Обновить аватар"
                    button="Сохранить"
                >
                    <input
                        id="avatar"
                        type="url"
                        className="popup__input popup__input_type_avatar"
                        name="avatar"
                        placeholder="Ссылка на аватар"
                        required
                        minLength="2"
                    />
                    <span className="popup__form-error popup__form-error-avatar"></span>
                </PopupWithForm>
                <PopupWithForm
                    name="delete"
                    title="Вы уверены?"
                    button="Да"
                ></PopupWithForm>
                <ImagePopup card={selectedCard} onClose={closeAllPopups} />
            </div>
        </div>
        </CurrentUserContext.Provider>
    );
}
