import React from "react";

export default function PopupWithForm({name, title, isOpen, children, button}) {
    <>
    <section className={`popup popup_type_${name} ${isOpen && 'popup_opened'}`}>
        <div className="popup__container">
            <button type="button" class="popup__close"></button>
            <h2 className="popup__title">{title}</h2>
            <form name="form" className="popup__form" novalidate>
                <fieldset className="popup__fieldset">
                    <input value="" id="nameInput" type="text" className="popup__input popup__input_user_name" name={name} placeholder="Имя пользователя" required minlength="2" maxlength="40" />
                    {children}
                    <input value="" id="jobInput" type="text" className="popup__input popup__input_user_job" name="about" placeholder="Род деятельности" required minlength="2" maxlength="200" />
                    {children}
                    <button className="popup__button popup__button_edit" type="submit" disabled>{button}</button>
                 </fieldset>
             </form>
        </div>
    </section>
    </>
}

